<script lang="ts">
  export let type: string;

  export let color = '';

  export let size = 0;
  export let large = false;
  export let medium = false;
  export let small = false;

  const sizes = {
    large: 48,
    medium: 32,
    small: 16,
  }

  type Name = keyof typeof sizes
  
  let cssText = ''
  $: {
    const pieces = []
    if(color) {
      pieces.push('color: ' + (color.indexOf('--') === 0 ? `var(${color})` : color));
    }

    const width = large ? 32 : (medium ? 24 : (small ? 16 : size));

    if(width > 0) {
      pieces.push(`font-size: ${width}px`)
    }

    cssText = pieces.join(';')
  }
</script>

<i class={`icon-${type}`} style={cssText}></i>

